mapper.select:

select * from (select rownum rnum, approval.* from 
(select * from approval order by appNum desc) approval) 
		where rnum >= #{start} and rnum <=#{end}


select * from (select rownum rnum, approval.* from 
(select * from approval where name1=#{id} or name2=#{id} or name3=#{id} order by appNum desc) approval) 
		where rnum >= #{start} and rnum <=#{end}


select * from (select rownum rnum, approval.* from 
(select * from approval where ${sel} like '%'||#{search}||'%' order by appNum desc) approval) 
		where rnum >= #{start} and rnum <=#{end}


