mapper.select:

select B.*, r from
	(select rownum r, A.* from
		(select * from mail where ${sel} like '%'||#{search}||'%' and mailRid = #{id}
		order by mailreg desc)A 
	order by mailReg desc) B 
where r >= #{start} and r <= #{end}


select B.*, r from
	(select rownum r, A.* from
		(select * from mail where ${sel} like '%'||#{search}||'%' and userId = #{id}
		order by mailreg desc)A 
	order by mailReg desc) B 
where r >= #{start} and r <= #{end}
